import { useContext, useEffect } from 'react';
import { EmployeeContext } from './EmployeeContext.js';
import './EmployeeManagement.css';

function EmployeeManagement() {

  const { employeeList, selectedEmployees, setSelectedEmployees } = useContext(EmployeeContext)

  const handleSubmit = () =>{
      console.log();
  }

  useEffect(() =>{
    let selectedArr = employeeList
    ?.map( obj => obj["checked"])
    .filter( bool => bool )

    setSelectedEmployees(selectedArr?.length)
  }, [employeeList])

  return (
      <div className="employee-management-div">
        <span>
            <select className="employee-updates-dropdown">
                <option value="delete">Delete</option>
                {selectedEmployees?.length === 1 ? <option value="edit">Edit</option>  : null}
            </select>
            <button onClick={handleSubmit}>Submit</button>
        </span>
        <span>{selectedEmployees?.length > 0? selectedEmployees?.length : "0"} item selected</span>

      </div>
  );
}

export default EmployeeManagement;
