import { useContext, useEffect, useState } from 'react';
import { EmployeeContext } from './EmployeeContext.js';
import { Link } from 'react-router-dom';
import './EmployeeManagement.css';

function EmployeeManagement() {

  const { employeeList, selectedEmployees, setSelectedEmployees } = useContext(EmployeeContext)

  const [dropDownOption, setDropDownOption] = useState('delete')

  const handleSubmit = (e) =>{
    e.preventDefault();
    if (dropDownOption==='delete'){
      console.log('delete item from the list');
    } else if (dropDownOption==='edit'){
      console.log('edit item from the list');
    }
  }

  useEffect(() =>{
    let selectedArr = employeeList
    ?.map( obj => obj["checked"])
    .filter( bool => bool )

    setSelectedEmployees(selectedArr?.length)
  }, [employeeList])

  return (
      <div className="employee-management-div">

        <span>
          {selectedEmployees > 0 ? selectedEmployees : "0"} item selected
        </span>

        <span>
          <form onClick={handleSubmit}>
            <select 
              onChange={(e) => {setDropDownOption(e.target.value)}} className="employee-updates-dropdown">
                <option value="delete">Delete</option>
                {selectedEmployees === 1 ? <option value="edit">Edit</option>  : null}
            </select>
            <button className="submit-btn"> Submit </button>
          </form>


        </span>

        <span>
          <button>
          <Link to="/new-employee">Add New Employee</Link>
          </button>
        </span>

      </div>
  );
}

export default EmployeeManagement;
