import { useContext } from 'react';
import { EmployeeContext } from './EmployeeContext.js'
import './Traits.css'

function Traits(){
    const {
        traitsList,
        userTraits,
        setUserTraits
      } = useContext(EmployeeContext);

    const handleCheckBox = (e, trait) =>{ 
        const checked = e.target.checked

        if (checked) {
            console.log("add trait to user trait list")
            setUserTraits([...userTraits, trait.id])
        } else {
            console.log("remove trait to user trait list")
            // find the index of the trait.id in the userTraits list 
            const idx = userTraits.indexOf(trait.id)

            if (idx > -1){
                setUserTraits( [...userTraits.slice(0,idx)] )
            }
        }

        // console.log(trait)
    }

    return (
        <div>
            {traitsList ?
                traitsList.map(trait =>
                    <div 
                        key={trait.id} className="trait-container">
                        <input 
                        className="trait-checkbox" 
                        onClick={ (e) => handleCheckBox(e, trait)}
                        type="checkbox" label={trait.id}/>
                        <label>
                            <span>
                                <img className="icon-image" src={ require(`./assets/${trait.icon}`).default } />
                            </span>
                            {trait.trait}
                        </label>
                    </div>) :
                null
            }
        </div>
    )
}

export default Traits;
