import { useState, useContext, useEffect } from 'react';
import { Redirect } from 'react-router-dom'
import { EmployeeContext } from './EmployeeContext.js'
import './NewEmployeeForm.css';
import axios from 'axios';

function NewEmployeeForm(){

    const {
        employeeList,
        setEmployeeList
      } = useContext(EmployeeContext);

    const [user, setUser] = useState()
    const [redirect, setRedirect] = useState(false)

    useEffect(() => {
        axios
          .get("/api/traits")
          .then((res) => {
            setEmployeeList(
              res.data.map(obj=> ({...obj, checked: false}))
            )
          }
            );
    }, [setEmployeeList]);

    const submit = e => {
        e.preventDefault()
        axios('/api/employee', {
        method: 'POST',
        data: JSON.stringify( user ),
        headers: { 'Content-Type': 'application/json' },
        })
            .then(res => {
                setEmployeeList([...employeeList, {...res.data.employee, checked: false}])
                setRedirect(true)
            })


    }

    if (redirect) {
        return <Redirect to='/'/>;
    }

    return (
    <div className="new-employee-form-container">
        <form className="new-employee-form" onSubmit={submit}>
            <div className="form-grid-container">
                <div className="col-one-container">
                    <input
                        type="text"
                        placeholder="First Name"
                        onChange={e => setUser({ ...user, first_name: e.target.value })}
                    />

                    <input
                        type="text"
                        placeholder="Last Name"
                        onChange={e => setUser({ ...user, last_name: e.target.value })}
                    />

                    <input
                        type="text"
                        placeholder="Email Address"
                        onChange={e => setUser({ ...user, email_address: e.target.value })}
                    />

                    <input
                        type="text"
                        placeholder="Job"
                        onChange={e => setUser({ ...user, job: e.target.value })}
                    />
                </div>
                <div className="col-two-container">
                    <div className="trait-container">
                        <input 
                            className="trait-checkbox" 
                            type="checkbox" label="coffee-lover"/>
                        <label>
                            <span>
                                <img className="icon-image" src={ require('./assets/coffee-cup.png').default } />

                            </span>
                            Coffee lover
                        </label>
                    </div>
                </div>
            </div>



            <button>Create</button>
        </form>
    </div>


    )
}

export default NewEmployeeForm
