import {useContext, useEffect} from 'react';
import {EmployeeContext} from './EmployeeContext.js'
import './EmployeeTable.css';

function EmployeeTable() {


    const {
        employeeList, setEmployeeList,
        selectedEmployees, setSelectedEmployees
    } = useContext(EmployeeContext);

  useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((employeeList) => {
        setEmployeeList(employeeList)
        console.log(employeeList[0]['first_name'])
      }
        );
  }, []);

  const handleSelectEmployee = (e) => {
    // e.target.checked ? setSelectedCount(selectedCount+1) : setSelectedCount(selectedCount-1)
  }

  const handleSelectAllEmployees = (e) => {
    console.log('check all');
    if (e.target.checked){
      setSelectedEmployees(
        employeeList.map(obj => obj.id)
      )
    } else {
      setSelectedEmployees()
    }

  }

  return (
      <table className="employee-table">
        <thead>
            <tr>
            <th> <input onChange={(e) => handleSelectAllEmployees(e)} type="checkbox" /> </th>
            <th>Customer ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email Address</th>
            <th>Role</th>
            </tr>
        </thead>
        <tbody>
            {employeeList ?
                employeeList.map( obj => 
                <tr>
                    <td>
                      <input 
                        checked={ employeeList.length === selectedEmployees.length ? 'checked': ''}
                        onClick={(e) => handleSelectEmployee(e)} 
                        type="checkbox" />
                      </td>
                    <td>{obj.id}</td>
                    <td>{obj.first_name}</td>
                    <td>{obj.last_name}</td>
                    <td>{obj.email_address}</td>
                    <td>{obj.job}</td>
                </tr>  
                ) :
                <tr>loading....</tr>
            }
        </tbody>
      </table>
  );
}

export default EmployeeTable;
