import {useState, useContext} from 'react';

function EditEmployeeForm(){

    const {
        employeeList,
        editEmployee
      } = useContext(EmployeeContext);

    const [redirect, setRedirect] = useState(false)
    const [user, setUser] = useState()

    const submit = e => {
        e.preventDefault()

        //update user id @ value
        axios('/api', {
        method: 'POST',
        data: JSON.stringify( user ),
        headers: { 'Content-Type': 'application/json' },
        })
            .then(res => {

                //set employee list to be current employ list find the index of the rid

                setEmployeeList([...employeeList, {...res.data.employee, checked: false}])
                setRedirect(true)
            })


    }

    console.log(editEmployee)

    if (redirect) {
        return <Redirect to='/'/>;
    }

    return (
    <div className="new-employee-form">
        <form onSubmit={submit}>
            <input
                type="text"
                placeholder="First Name"
                onChange={e => setUser({ ...user, first_name: e.target.value })}
            />

            <input
                type="text"
                placeholder="Last Name"
                onChange={e => setUser({ ...user, last_name: e.target.value })}
            />

            <input
                type="text"
                placeholder="Email Address"
                onChange={e => setUser({ ...user, email_address: e.target.value })}
            />

            <input
                type="text"
                placeholder="Job"
                onChange={e => setUser({ ...user, job: e.target.value })}
            />

            <input type="submit" name="Sign Up" />
        </form>
    </div>

    )
}

export default EditEmployeeForm;
