import { useContext } from 'react';
import EmployeeTable from './EmployeeTable.js';
import './App.css';
import { EmployeeContext } from './EmployeeContext.js';

function App() {

  const { selectedCount } = useContext(EmployeeContext)

  return (
    <div className="App">
      <div className="employee-management-div">
        <span>{selectedCount > 0? `${selectedCount} item selected` : ""}</span>
        <span>
        <select class="employee-updates-dropdown">
          <option value="volvo">Delete</option>
          <option value="saab">Edit</option>
        </select>
        </span>
      </div>
      <EmployeeTable />
    </div>
  );
}

export default App;
