import {useContext, useEffect} from 'react';
import {EmployeeContext} from './EmployeeContext.js'
import axios from 'axios';
import './EmployeeTable.css';

function EmployeeTable() {


    const {
        employeeList, setEmployeeList,
        selectAll, setSelectAll
    } = useContext(EmployeeContext);

  useEffect(() => {
    axios
      .get("/api")
      .then((res) => {
        setEmployeeList(
          res.data.map(obj=> ({...obj, checked: false}))
        )
      }
        );
  }, []);

  const handleSelectEmployee = (idx, event) => {
    if (event.target.checked){
      setEmployeeList(
        [...employeeList.slice(0,idx), {...employeeList[idx], checked: true}, ...employeeList.slice(idx+1)]
      )
    } else {
      setEmployeeList(
        [...employeeList.slice(0,idx), {...employeeList[idx], checked: false}, ...employeeList.slice(idx+1)]
      )
    }

          // if all the items are checked set select all true
      // else 
      // set select all false
  }

  const handleSelectAllEmployees = (event) => {
    if (event.target.checked){
      setSelectAll(true)
      setEmployeeList(
        employeeList.map(obj=> ({...obj, checked: true}))
      )
    } else {
      setSelectAll(false)
      setEmployeeList(
        employeeList.map(obj=> ({...obj, checked: false}))
      )
    }
  }

  return (
      <table className="employee-table">
        <thead>
            <tr>
            <th> 
              <input 
                checked={selectAll ? 'checked' : ''}
                onClick={(event) => handleSelectAllEmployees(event)} 
                type="checkbox" 
              /> 
            </th>
            <th>Customer ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email Address</th>
            <th>Role</th>
            </tr>
        </thead>
        <tbody>
            {employeeList ?
                employeeList.map( (obj, idx) => 
                <tr>
                    <td>
                      <input 
                        checked={obj.checked ? 'checked ': ''}
                        onClick={(event) => handleSelectEmployee(idx, event)} 
                        type="checkbox" />
                      </td>
                    <td>{obj.id}</td>
                    <td>{obj.first_name}</td>
                    <td>{obj.last_name}</td>
                    <td>{obj.email_address}</td>
                    <td>{obj.job}</td>
                </tr>  
                ) :
                <tr>loading....</tr>
            }
        </tbody>
      </table>
  );
}

export default EmployeeTable;
